+                do {
+                    nextToken();
+                    JCPattern nestedPattern = parsePattern();
+                    nested.append(nestedPattern);
+                } while (token.kind == COMMA);
+                accept(RPAREN);
+                return toP(F.at(pos).DeconstructionPattern(e, nested.toList()));
+            } else {
+                return toP(F.at(pos).BindingPattern(ident(), e));
+            }
+        }
+    }
