+bool LogConfiguration::is_fd_used(int fd) {
+  assert(fd != -1, "fd must be valid");
+  for (size_t i = 0; i < _n_outputs; i++) {
+    if (fd == _outputs[i]->fd_get()) {
+      return true;
+    }
+  }
+  return false;
+}
+
+void LogConfiguration::close() {
+  for (size_t i = 0; i < _n_outputs; i++) {
+    _outputs[i]->close();
+  }
+}
+
+void LogConfiguration::reopen() {
+  for (size_t i = 0; i < _n_outputs; i++) {
+    _outputs[i]->reopen();
+  }
+}

