+#define SSS_NSS_MEMCACHE "/var/lib/sss/mc/"
+    if (!strncmp(details, SSS_NSS_MEMCACHE, sizeof(SSS_NSS_MEMCACHE) - 1)) {
+      print_resources("OK: SSSD cache\n");
+      continue;
+    }
