+          excessive_CPU |= CPU_SSE4_2;
+        }
+        if ((_features & CPU_SSE3) &&
+            (_glibc_features & GLIBC_CMPXCHG16) &&
+            (_glibc_features & GLIBC_LAHFSAHF)) {
