+int os::rmdir(const char *path) {
+  errno_t err;
+  wchar_t* wide_path = wide_abs_unc_path(path, err);
+  if (wide_path == NULL) {
+    errno = err;
+    return -1;
+  }
+  int res = _wrmdir(wide_path);
+  os::free(wide_path);
+  return res;
+}
