+package org.openjdk.jcstress;
+
+import org.openjdk.jcstress.infra.grading.ReportUtils;
+import org.openjdk.jcstress.util.TimeValue;
+
+import java.io.PrintStream;
+import java.util.concurrent.TimeUnit;
+
+public class TimeBudget {
+
+    final long endTime;
+    final boolean zeroBudget;
+    final int expectedIterations;
+
+    int completedIterations;
+
+    public TimeBudget(int expectedIterations, TimeValue timeBudget) {
+        this.expectedIterations = expectedIterations;
+        this.endTime = TimeUnit.NANOSECONDS.toMillis(System.nanoTime()) + timeBudget.milliseconds();
+        this.zeroBudget = timeBudget.isZero();
+    }
+
+    public void reportIteration() {
+        completedIterations++;
+    }
+
+    public int targetIterTimeMs() {
+        if (isZero()) {
+            return 0;
+        }
+
+        int leftoverIterations = expectedIterations - completedIterations;
+        long timeLeft = timeLeftMs();
+
+        int msPerIter = (timeLeft > 0 && leftoverIterations > 0) ?
+                (int) (timeLeft / leftoverIterations) :
+                0;
+
+        // Reserve a bit of iteration time for infrastructure, but never drop below 1ms
+        msPerIter = Math.max(1, msPerIter - 30);
+        return msPerIter;
+    }
+
+    public long timeLeftMs() {
+        return endTime - TimeUnit.NANOSECONDS.toMillis(System.nanoTime());
+    }
+
+    public void printOn(PrintStream out) {
+        out.println("  Time budget:");
+        if (isZero()) {
+            out.println("    Zero budget, sanity test mode");
+        } else {
+            out.println("    Target completion: in " + ReportUtils.msToDate(timeLeftMs(), true));
+            out.println("    Initial iteration time: " + targetIterTimeMs() + " ms");
+        }
+        out.println();
+    }
+
+    public boolean isZero() {
+        return zeroBudget;
+    }
+}

