+    private void awaitNotification() {
+        synchronized (notifyLock) {
+            try {
+                // Wait one second and then unblock for extra safety
+                notifyLock.wait(1000);
+            } catch (InterruptedException e) {
+                // Do nothing
+            }
+        }
+    }
+
+    private void notifyChanged() {
+        synchronized (notifyLock) {
+            notifyLock.notify();
+        }
+    }
