+package org.openjdk.jcstress;
+
+import org.objectweb.asm.*;
+
+import java.io.IOException;
+import java.nio.file.*;
+import java.nio.file.attribute.BasicFileAttributes;
+
+
+public class ContendedAutoInjectorMain {
+
+    public static void main(String[] args) throws IOException {
+        if (args.length >= 1) {
+            String path = args[0];
+            System.out.println("Processing classes at " + path);
+
+            Files.walkFileTree(Path.of(path), new FileVisitor<>() {
+                @Override
+                public FileVisitResult visitFile(Path path, BasicFileAttributes basicFileAttributes) throws IOException {
+                    if (path.toString().endsWith("class")) {
+                        byte[] origBytes = Files.readAllBytes(path);
+
+                        if (hasStateAnnotation(origBytes)) {
+                            System.out.println("Injecting @Contended into " + path);
+                            final byte[] newBytes = retransform(origBytes);
+                            Files.write(path, newBytes);
+                        }
+                    }
+                    return FileVisitResult.CONTINUE;
+                }
+
+                @Override
+                public FileVisitResult visitFileFailed(Path path, IOException e) {
+                    return FileVisitResult.CONTINUE;
+                }
+
+                @Override
+                public FileVisitResult preVisitDirectory(Path path, BasicFileAttributes basicFileAttributes) {
+                    return FileVisitResult.CONTINUE;
+                }
+
+                @Override
+                public FileVisitResult postVisitDirectory(Path path, IOException e) {
+                    return FileVisitResult.CONTINUE;
+                }
+            });
+        } else {
+            throw new IllegalStateException("Please provide the destination dir");
+        }
+    }
+
+    private static byte[] retransform(byte[] origBytes) {
+        ClassReader cr = new ClassReader(origBytes);
+        ClassWriter cw = new ClassWriter(cr, 0);
+        ContendedAnnotator ca = new ContendedAnnotator(cw);
+        cr.accept(ca, 0);
+        return cw.toByteArray();
+    }
+
+    static class ContendedAnnotator extends ClassVisitor {
+        public ContendedAnnotator(ClassWriter cw) {
+            super(Opcodes.ASM4, cw);
+        }
+
+        @Override
+        public FieldVisitor visitField(int access, String name, String desc, String signature, Object value) {
+            FieldVisitor fv = super.visitField(access, name, desc, signature, value);
+            fv.visitAnnotation("Lsun/misc/Contended;", true);
+            fv.visitAnnotation("Ljdk/internal/vm/annotation/Contended;", true);
+            return fv;
+        }
+    }
+
+    private static boolean hasStateAnnotation(byte[] origBytes) {
+        ClassReader cr = new ClassReader(origBytes);
+        StateDetector sd = new StateDetector();
+        cr.accept(sd, 0);
+        return sd.isDetected();
+    }
+
+
+    static class StateDetector extends ClassVisitor {
+        private boolean detected;
+
+        public StateDetector() {
+            super(Opcodes.ASM6);
+        }
+
+        @Override
+        public AnnotationVisitor visitAnnotation(String desc, boolean visible) {
+            if (desc.equals("Lorg/openjdk/jcstress/annotations/State;")) {
+                detected = true;
+            }
+            return super.visitAnnotation(desc, visible);
+        }
+
+        public boolean isDetected() {
+            return detected;
+        }
+    }
+}

