+  Register tmp1 = noreg, tmp2 = noreg;
@@ -301,2 +301,6 @@
-        tmp1 = r;
-        break;
+        if (tmp1 == noreg) {
+          tmp1 = r;
+        } else {
+          tmp2 = r;
+          break;
+        }
@@ -306,0 +310,1 @@
