+            A.setSum(temp);                    // A += (temp | 0x01)
+            A.setProduct(R);                   // A =  (A * R) % p
+            offset += BLOCK_LENGTH;
+            length -= BLOCK_LENGTH;
+        }
+
+        A.asByteArray(aBytes);
+    }
