+void ADLParser::peep_predicate_parse(Peephole& peep) {
+
+  skipws();
+  char* rule = nullptr;
+  if ( (rule = get_paren_expr("pred expression", true)) == nullptr ) {
+    parse_err(SYNERR, "incorrect or missing expression for 'peeppredicate'\n");
