+  if (_curchar != ';') {
+    parse_err(SYNERR, "missing ';' in peeppredicate definition\n");
+    return;
+  }
+  next_char();   // skip ';'
+  skipws();
+
+  // Construct PeepPredicate
+  PeepPredicate* predicate = new PeepPredicate(rule);
+  peep.add_predicate(predicate);
+}
