+package org.openjdk.bench.vm.compiler.x86;
+
+import org.openjdk.jmh.annotations.*;
+import org.openjdk.jmh.infra.Blackhole;
+
+import java.util.concurrent.TimeUnit;
+
+@BenchmarkMode(Mode.AverageTime)
+@OutputTimeUnit(TimeUnit.NANOSECONDS)
+@Fork(value = 1)
+@State(Scope.Thread)
+public class LeaPeephole {
+    static final int ITERATION = 1000;
+
+    int x, y;
+
+    @Benchmark
+    public void B_I_int(Blackhole bh) {
+        int x = this.x;
+        int y = this.y;
+        for (int i = 0; i < ITERATION; i++) {
+            int x1 = x + y;
+            x = x1 + y;
+            y = x1 + x;
+        }
+        bh.consume(x);
+        bh.consume(y);
+    }
+
+    @Benchmark
+    public void B_D_int(Blackhole bh) {
+        int x = this.x;
+        int y = this.y;
+        for (int i = 0; i < ITERATION; i++) {
+            bh.consume(x + 10);
+            bh.consume(x + 20);
+            bh.consume(x + 30);
+            bh.consume(y + 10);
+            bh.consume(y + 20);
+            bh.consume(y + 30);
+            x = x >> 1;
+            y = y >> 2;
+        }
+    }
+
+    @Benchmark
+    public void I_S_int(Blackhole bh) {
+        int x = this.x;
+        int y = this.y;
+        for (int i = 0; i < ITERATION; i++) {
+            bh.consume(x << 1);
+            bh.consume(x << 2);
+            bh.consume(x << 3);
+            bh.consume(y << 1);
+            bh.consume(y << 2);
+            bh.consume(y << 3);
+            x = x >> 1;
+            y = y >> 2;
+        }
+    }
+
+    @Benchmark
+    public void B_I_long(Blackhole bh) {
+        long x = this.x;
+        long y = this.y;
+        for (int i = 0; i < ITERATION; i++) {
+            long x1 = x + y;
+            x = x1 + y;
+            y = x1 + x;
+        }
+        bh.consume(x);
+        bh.consume(y);
+    }
+
+    @Benchmark
+    public void B_D_long(Blackhole bh) {
+        long x = this.x;
+        long y = this.y;
+        for (int i = 0; i < ITERATION; i++) {
+            bh.consume(x + 10);
+            bh.consume(x + 20);
+            bh.consume(x + 30);
+            bh.consume(y + 10);
+            bh.consume(y + 20);
+            bh.consume(y + 30);
+            x = x >> 1;
+            y = y >> 2;
+        }
+    }
+
+    @Benchmark
+    public void I_S_long(Blackhole bh) {
+        long x = this.x;
+        long y = this.y;
+        for (int i = 0; i < ITERATION; i++) {
+            bh.consume(x << 1);
+            bh.consume(x << 2);
+            bh.consume(x << 3);
+            bh.consume(y << 1);
+            bh.consume(y << 2);
+            bh.consume(y << 3);
+            x = x >> 1;
+            y = y >> 2;
+        }
+    }
