@@ -31,0 +31,1 @@
+import jdk.internal.misc.Unsafe;
@@ -36,0 +37,4 @@
+    private static final Unsafe unsafe = Unsafe.getUnsafe();
+    private static final long accessorGeneratedaOffset
+        = unsafe.objectFieldOffset(NativeConstructorAccessorImpl.class, "accessorGenerated");
+
@@ -39,0 +44,1 @@
+    boolean accessorGenerated;
@@ -54,8 +60,17 @@
-                && !ReflectUtil.isVMAnonymousClass(c.getDeclaringClass())) {
-            ConstructorAccessorImpl acc = (ConstructorAccessorImpl)
-                new MethodAccessorGenerator().
-                    generateConstructor(c.getDeclaringClass(),
-                                        c.getParameterTypes(),
-                                        c.getExceptionTypes(),
-                                        c.getModifiers());
-            parent.setDelegate(acc);
+                && !ReflectUtil.isVMAnonymousClass(c.getDeclaringClass())
+                && accessorGenerated == false
+                && unsafe.compareAndSetBoolean(this, accessorGeneratedaOffset, false, true)) {
+            try {
+                ConstructorAccessorImpl acc = (ConstructorAccessorImpl)
+                    new MethodAccessorGenerator().
+                        generateConstructor(c.getDeclaringClass(),
+                                            c.getParameterTypes(),
+                                            c.getExceptionTypes(),
+                                            c.getModifiers());
+                parent.setDelegate(acc);
+            } catch (Throwable t) {
+                // in case Thowable happens in generateMethod
+                // restore accessorGenerated flag to false
+                accessorGenerated = false;
+                throw t;
+            }

