+  Node* bol = cmove->as_CMove()->in(CMoveNode::Condition);
+  if (my_pack(bol)) {
+    remove_pack(my_pack(bol));
+  }
+  Node* cmp = bol->in(1);
+  if (my_pack(cmp)) {
+    remove_pack(my_pack(cmp));
+  }
+  remove_pack(cmove_pk);
