+  // #3: Test JVMTI SetLocalLong function
+  err = jvmti->SetLocalLong(thread, depth, SlotLong, ll);
+  check_jvmti_error_not_suspended(jni, "SetLocalLong", err);
+  LOG("check for JVMTI SetLocalLong succeeded\n");
