+// Acquire symbols from method and field entries
+// For fields and methods
+char* name_symbol_at(int which) { return symbol_at(name_ref_index_at(uncached_name_and_type_ref_index_at(which)))->as_C_string(); }
+char* signature_symbol_at(int which) { return symbol_at(signature_ref_index_at(uncached_name_and_type_ref_index_at(which)))->as_C_string(); }
+char* klass_name_symbol_at(int which) {
+  EXCEPTION_MARK;
+  Klass* k = klass_at(uncached_klass_ref_index_at(which), CATCH);
+  return  k->name()->as_C_string();
+}
+// Get class name from invokedynamic
+char* klass_name_symbol_invokedynamic_at(int which) {
+  EXCEPTION_MARK;
+  int index = uncached_klass_ref_index_at(method_handle_index_at(bootstrap_method_ref_index_at(which)));
+  Klass* k = klass_at(index, CATCH);
+  guarantee(k != NULL, "need klass");
+  return k->name()->as_C_string();
+}
+char* name_symbol_invokedynamic_at(int which) {
+  return name_symbol_at(method_handle_index_at(bootstrap_method_ref_index_at(which)));
+}
+char* signature_symbol_invokedynamic_at(int which) {
+  return signature_symbol_at(method_handle_index_at(bootstrap_method_ref_index_at(which)));
+}
