+package nsk.share.jdi;
+
+public class Utils {
+
+    public static boolean isAlive(Thread thread) {
+         for (int attempt = 1; attempt <= 5; attempt++) {
+             if (!thread.isAlive()) {
+                 return false;
+             }
+             try {
+                 Thread.sleep(attempt * 1000);
+             } catch (InterruptedException ie) {
+             }
+         }
+         return true;
+    }
+}
