@@ -7,1 +7,1 @@
-The updated info comes from the LICENSE and TRADEMARK files.
+The updated info comes from the LICENSE, TRADEMARK and AUTHORS files.
@@ -40,13 +40,12 @@
-    Use expand to remove tabs
-        for f in *.c *.h;
-        do
-        expand ${f} > ${f}.tmp;
-        mv ${f}.tmp $f;
-        done
-
-    Use sed to remove trailing white space
-        for f in *.c *.h;
-        do
-        sed -e 's/[ ]* $//' ${f} > ${f}.tmp;
-        mv ${f}.tmp $f;
-        done
+    for f in *.c *.h;
+    do
+      # replace tabs with spaces
+      expand ${f} > ${f}.tmp;
+      mv ${f}.tmp $f;
+
+      # fix line endings to LF
+      sed -i -e 's/\r$//g' ${f};
+
+      # remove trailing spaces
+      sed -i -e 's/[ ]* $//g' ${f};
+    done
