+        checkInferedType("A<?>", "B", "B<?>");
+        checkInferedType("A<?>", "C", "C<?,?>");
+        checkInferedType("A<?>", "D", "D<?,?>");
+        checkInferedType("A<?>", "E", "E<?>");
+        checkInferedType("A<?>", "F", "F<?>");
+        checkInferedType("A<?>", "G", "G<?>");
+        checkInferedType("A<?>", "H", "H");
+        checkInferedType("A<?>", "I", "I<?>");
