+void Assembler::testw(Register dst, int16_t imm16) {
+  int encode = dst->encoding();
+  InstructionMark im(this);
+  emit_int8(0x66);
+  if (encode == 0) {
+    emit_int8((unsigned char)0xA9);
+  } else {
+    encode = prefix_and_encode(encode);
+    emit_int16((unsigned char)0xF7, (0xC0 | encode));
+  }
+  emit_int16(imm16);
+}
