+bool VM_GetOrSetLocal::doit_prologue() {
+  if (_eb.barrier_active()) {
+    _jvf = get_java_vframe();
+    NULL_CHECK(_jvf, false);
+
+    if (!deoptimize_objects(_jvf)) {
+      return false;
+    }
+  }
+
+  return true;
+}
