+            int callCount = COMPILE_THRESHOLD + 1000;
+            doLoop = true;
+            while (callCount-- > 0) {
+                dontinline_testMethod();
+            }
+            warmUpDone = true;
+            msg("WarmUp: DONE");
+        }
+
+        public Object dontinline_endlessLoop(Object argEscape) {
+            long cs = checkSum;
+            while (loopCount-- > 0 && doLoop) {
+                targetIsInLoop = true;
+                checkSum += checkSum % ++cs;
+            }
+            loopCount = 3;
+            targetIsInLoop = false;
+            return argEscape;
+        }
