+                             jlong* tag_ptr,
+                             jint length,
+                             void* d) {
+  Tag_And_Counter* data = (Tag_And_Counter*) d;
+  if (class_tag == data->class_tag && *tag_ptr == 0) {
+    data->instance_counter++;
+    *tag_ptr = data->instance_tag;
+  }
+  return JVMTI_VISIT_OBJECTS;
+}
