+import javax.swing.JButton;
+import javax.swing.JFileChooser;
+import javax.swing.SwingUtilities;
+import javax.swing.UIManager;
+
+/*
+ * @test
+ * @bug 8300084
+ * @key headful
+ * @summary Test to check if getDefaultButton() return valid default approve button.
+ * @requires (os.family == "mac")
+ * @run main AquaDefaultButtonTest
+ */
+
+public class AquaDefaultButtonTest {
+
+    public static void main(String[] args) throws Exception {
+        UIManager.setLookAndFeel("com.apple.laf.AquaLookAndFeel");
+        SwingUtilities.invokeAndWait(new Runnable() {
+            public void run() {
+                JFileChooser fileChooser = new JFileChooser();
+                JButton defaultApproveButton = fileChooser.getUI().getDefaultButton(fileChooser);
+                if (defaultApproveButton == null) {
+                    throw new RuntimeException("getDefaultButton() method returns null for Aqua L&F!");
+                }
+            }
+        });
+        System.out.println("Test Pass!");
+    }
+}
