+
+#if defined(_ARCH_PWR7) && defined(_ARCH_PPC64)
+
+// __store8r is only available on POWER7 and newer in 64-bit mode.
+
+template <typename T>
+struct ByteswapImpl<T, 8> final {
+  STATIC_ASSERT(CanByteswapImpl<T>::value);
