+    unsigned long long y;
+    __store8r(static_cast<unsigned long long>(x), &y);
+    return static_cast<T>(y);
+  }
+};
+
+#else
+
+template <typename T>
+struct ByteswapImpl<T, 8> final : public ByteswapFallbackImpl<T, 8> {};
+
+#endif
+
