+  test_byteswap<int64_t>();
+  test_byteswap<uint64_t>();
+  test_byteswap<int32_t>();
+  test_byteswap<uint32_t>();
+  test_byteswap<int16_t>();
+  test_byteswap<uint16_t>();
+  test_byteswap<int8_t>();
+  test_byteswap<uint8_t>();
+}

