@@ -107,1 +110,9 @@
-int main(int argc, const char** argv) {
+typedef struct {
+     int argc;
+     char **argv;
+} args_list;
+
+void* run(void* argp){
+  args_list *arg = (args_list*) argp;
+  int argc =  arg->argc;
+  char **argv = arg->argv;
@@ -242,0 +253,21 @@
+int main(int argc, char *argv[]) {
+    args_list args;
+    args.argc = argc;
+    args.argv = argv;
+#ifdef AIX
+    size_t adjusted_stack_size = 1024*1024;
+    pthread_t id;
+    int result;
+    pthread_attr_t attr;
+    pthread_attr_init(&attr);
+    pthread_attr_setstacksize(&attr, adjusted_stack_size);
+    result = pthread_create(&id, &attr, run, (void *)&args);
+    if (result != 0) {
+      fprintf(stderr, "Error: pthread_create failed with error code %d \n", result);
+      return -1;
+    }
+    pthread_join(id, NULL);
+#else
+    run(&args);
+#endif //AIX
+}

