+// vector load mask
+
+instruct vloadmask(vReg_V0 v0, vReg src, rFlagsReg cr) %{
+  match(Set v0 (VectorLoadMask src));
+  format %{ "vloadmask V0, $src\t# KILL cr" %}
+  ins_encode %{
+    BasicType bt = Matcher::vector_element_basic_type(this, $src);
+    __ rvv_vsetvli(bt, Matcher::vector_length_in_bytes(this, $src));
+    __ vmsne_vx(v0, as_VectorRegister($src$$reg), x0);
+  %}
+  ins_pipe(pipe_slow);
+%}
