files.patch:
@@ -1342,0 +1342,7 @@
+static jlong nanos_to_nanos_bounded(jlong nanos) {
+  if (nanos / NANOUNITS > MAX_SECS) {
+    nanos = jlong(MAX_SECS) * NANOUNITS;
+  }
+  return nanos;
+}
+
@@ -1566,1 +1573,1 @@
-    to_abstime(&abst, nanos, false, false);
+    to_abstime(&abst, nanos_to_nanos_bounded(nanos), false, false);

@@ -1985,1 +1985,8 @@
-  return sleep_nanos(millis_to_nanos(millis));
+  jlong nanos;
+  if (millis > max_jlong / NANOUNITS_PER_MILLIUNIT) {
+    // Conversion to nanos would overflow, saturate at max
+    nanos = max_jlong;
+  } else {
+    nanos = millis * NANOUNITS_PER_MILLIUNIT;
+  }
+  return sleep_nanos(nanos);
