+    int test_multiple_expr(Box<?> t) {
+        return switch(t) {
+            case Box(R1 _), Box(R2 _) -> 1;
+            default -> -2;
+        };
+    }
+
+    int test_unrolled_expr(Box<?> t) {
+        return switch(t) {
+            case Box(R1 _) -> 1;
+            case Box(R2 _) -> 0;
+            default -> -2;
+        };
+    }
+
+    int test_multiple_stat(Box<?> t) {
+        int ret = -1;
+        switch(t) {
+            case Box(R1 _), Box(R2 _):
+                ret = 1;
+                break;
+            default:
+                ret = -2;
+        }
+        return ret;
+    }
+
+    int test_unrolled_stat(Box<?> t) {
+        int ret = -1;
+        switch(t) {
+            case Box(R1 _):
+                ret = 1;
+                break;
+            case Box(R2 _):
+                ret = 0;
+                break;
+            default:
+                ret = -2;
+        }
+        return ret;
+    }
+
