files.patch:
@@ -4775,1 +4775,9 @@
-  return ::write(fd, buf, nBytes);
+  while(nBytes > 0) {
+    unsigned int len = nBytes > INT_MAX ? INT_MAX : nBytes;
+    ssize_t written_bytes = ::write(fd, buf, len);
+    if (written_bytes < 0) {
+      return written_bytes;
+    }
+    nBytes -= written_bytes;
+    buf += written_bytes;
+  }
