files.patch:
@@ -113,0 +113,1 @@
+# include <sys/prctl.h>
@@ -4649,0 +4650,7 @@
+  // Override the timer slack value if needed.
+  if (TimerSlack > 0) {
+    if (prctl(PR_SET_TIMERSLACK, TimerSlack) < 0) {
+      vm_exit_during_initialization("Setting timer slack failed: %s", os::strerror(errno));
+    }
+  }
+
