+    public static char[][] getb2c() {
+        char[][] b2c = new char[DecodeHolder.b2c.length][];
+        for (int i = 0; i < b2c.length; i++) {
+            b2c[i] = DecodeHolder.b2c[i] == null
+                ? null : DecodeHolder.b2c[i].clone();
+        }
+        return b2c;
+    }
