+        return LinuxNativePty.open(attr, size);
+    }
+
+    public int isatty(int fd) {
+        return LinuxNativePty.isatty(fd);
+    }
+
+    public String ttyname(int fd) {
+        return LinuxNativePty.ttyname(fd);
+    }
