+    static final class DigitCache {
+        @Stable
+        static final char[] HEX256;
+        static {
+            HEX256 = new char[256];
+            for (int i = 0; i < 256; i++) {
+                int hi = (i >> 4) & 15;
+                int lo = i & 15;
+                HEX256[i] = (char) (((hi < 10 ? '0' + hi : 'a' + hi - 10) << 8)
+                        + (lo < 10 ? '0' + lo : 'a' + lo - 10));
+            }
+        }
+    }
