@@ -125,2 +125,2 @@
-        while (notInterrupted.get() > 0 && System.currentTimeMillis() - startTime <= waitTime) {
-            synchronized (waitnotify) {
+        synchronized (waitnotify) {
+            while (notInterrupted.get() > 0 && System.currentTimeMillis() - startTime <= waitTime) {
@@ -128,6 +128,1 @@
-                    // Wait for a thread to be interrupted. We first need to recheck
