+// AVX2 Vector AES Galois Counter Mode implementation.
+//
+// Inputs:           Windows    |   Linux
+//   in         = rcx (c_rarg0) | rsi (c_rarg0)
+//   len        = rdx (c_rarg1) | rdi (c_rarg1)
+//   ct         = r8  (c_rarg2) | rdx (c_rarg2)
+//   out        = r9  (c_rarg3) | rcx (c_rarg3)
+//   key        = rdi           | r8  (c_rarg4)
+//   state      = r13           | r9  (c_rarg5)
+//   subkeyHtbl = r11           | r11
+//   counter    = rsi           | r12
+//
+// Output:
+//   rax - number of processed bytes
+address StubGenerator::generate_avx2_galoisCounterMode_AESCrypt() {
+  __ align(CodeEntryAlignment);
+  StubCodeMark mark(this, "StubRoutines", "galoisCounterMode_AESCrypt");
+  address start = __ pc();
+
+  const Register in = c_rarg0;
+  const Register len = c_rarg1;
+  const Register ct = c_rarg2;
+  const Register out = c_rarg3;
+  // and updated with the incremented counter in the end
+ #ifndef _WIN64
+  const Register key = c_rarg4;
+  const Register state = c_rarg5;
+  const Address subkeyH_mem(rbp, 2 * wordSize);
+  const Register subkeyHtbl = r11;
+  const Address counter_mem(rbp, 3 * wordSize);
+  const Register counter = r12;
+ #else
+  const Address key_mem(rbp, 6 * wordSize);
+  const Register key = rdi;
+  const Address state_mem(rbp, 7 * wordSize);
+  const Register state = r13;
+  const Address subkeyH_mem(rbp, 8 * wordSize);
+  const Register subkeyHtbl = r11;
+  const Address counter_mem(rbp, 9 * wordSize);
+  const Register counter = rsi;
+ #endif
+  __ enter();
+  // Save state before entering routine
+  __ push(r12);
+  __ push(r13);
+  __ push(r14);
+  __ push(r15);
+  __ push(rbx);
+#ifdef _WIN64
+  // on win64, fill len_reg from stack position
+  __ push(rsi);
+  __ push(rdi);
+  __ movptr(key, key_mem);
+  __ movptr(state, state_mem);
+#endif
+  __ movptr(subkeyHtbl, subkeyH_mem);
+  __ movptr(counter, counter_mem);
+
+  // Save rbp and rsp
+  __ push(rbp);
+  __ movq(rbp, rsp);
+  // Align stack
+  __ andq(rsp, -64);
+  __ subptr(rsp, 16 * longSize); // Create space on the stack for saving AES entries
+
+  aesgcm_avx2(in, len, ct, out, key, state, subkeyHtbl, counter);
+  __ vzeroupper();
+  __ movq(rsp, rbp);
+  __ pop(rbp);
+  // Restore state before leaving routine
+ #ifdef _WIN64
+  __ pop(rdi);
+  __ pop(rsi);
+ #endif
+  __ pop(rbx);
+  __ pop(r15);
+  __ pop(r14);
+  __ pop(r13);
+  __ pop(r12);
+
+  __ leave(); // required for proper stackwalking of RuntimeStub frame
+  __ ret(0);
+
+  return start;
+}
+
@@ -3180,0 +3293,970 @@
+void StubGenerator::gfmul_avx2(XMMRegister GH, XMMRegister HK) {
+  const XMMRegister T1 = xmm1;
+  const XMMRegister T2 = xmm2;
+  const XMMRegister T3 = xmm3;
+
+  __ vpclmulqdq(T1, GH, HK, 0x11); // %%T1 = a1*b1
+  __ vpclmulqdq(T2, GH, HK, 0x00); // %%T2 = a0*b0
+  __ vpclmulqdq(T3, GH, HK, 0x01); // %%T3 = a1*b0
+  __ vpclmulqdq(GH, GH, HK, 0x10); // %%GH = a0*b1
+  __ vpxor(GH, GH, T3, Assembler::AVX_128bit);
+
+  __ vpsrldq(T3, GH, 8, Assembler::AVX_128bit); // shift-R %%GH 2 DWs
+  __ vpslldq(GH, GH, 8, Assembler::AVX_128bit); // shift-L %%GH 2 DWs
+
+  __ vpxor(T1, T1, T3, Assembler::AVX_128bit);
+  __ vpxor(GH, GH, T2, Assembler::AVX_128bit);
+
+  //first phase of the reduction
+  __ movdqu(T3, ExternalAddress(ghash_polynomial_reduction_addr()), r15 /*rscratch*/);
+  __ vpclmulqdq(T2, T3, GH, 0x01);
+  __ vpslldq(T2, T2, 8, Assembler::AVX_128bit); // shift-L %%T2 2 DWs
+
+  __ vpxor(GH, GH, T2, Assembler::AVX_128bit); // first phase of the reduction complete
+  //second phase of the reduction
+  __ vpclmulqdq(T2, T3, GH, 0x00);
+  __ vpsrldq(T2, T2, 4, Assembler::AVX_128bit); // shift-R %%T2 1 DW (Shift-R only 1-DW to obtain 2-DWs shift-R)
+
+  __ vpclmulqdq(GH, T3, GH, 0x10);
+  __ vpslldq(GH, GH, 4, Assembler::AVX_128bit); // shift-L %%GH 1 DW (Shift-L 1-DW to obtain result with no shifts)
+
+  __ vpxor(GH, GH, T2, Assembler::AVX_128bit); // second phase of the reduction complete
+  __ vpxor(GH, GH, T1, Assembler::AVX_128bit); // the result is in %%GH
+}
+
+void StubGenerator::generateHtbl_8_block_avx2(Register htbl, Register rscratch) {
+  const XMMRegister HK = xmm6;
+
+  __ movdqu(HK, Address(htbl, 0));
+  __ movdqu(xmm1, ExternalAddress(ghash_long_swap_mask_addr()), rscratch);
+  __ vpshufb(HK, HK, xmm1, Assembler::AVX_128bit);
+
+  __ movdqu(xmm11, ExternalAddress(ghash_polynomial_addr()), rscratch);
+  __ movdqu(xmm12, ExternalAddress(ghash_polynomial_two_one_addr()), rscratch);
+  // Compute H ^ 2 from the input subkeyH
+  __ movdqu(xmm2, xmm6);
+  __ vpsllq(xmm6, xmm6, 1, Assembler::AVX_128bit);
+  __ vpsrlq(xmm2, xmm2, 63, Assembler::AVX_128bit);
+  __ movdqu(xmm1, xmm2);
+  __ vpslldq(xmm2, xmm2, 8, Assembler::AVX_128bit);
+  __ vpsrldq(xmm1, xmm1, 8, Assembler::AVX_128bit);
+  __ vpor(xmm6, xmm6, xmm2, Assembler::AVX_128bit);
+
+  __ vpshufd(xmm2, xmm1, 0x24, Assembler::AVX_128bit);
+  __ vpcmpeqd(xmm2, xmm2, xmm12, Assembler::AVX_128bit);
+  __ vpand(xmm2, xmm2, xmm11, Assembler::AVX_128bit);
+  __ vpxor(xmm6, xmm6, xmm2, Assembler::AVX_128bit);
+  __ movdqu(Address(htbl, 1 * 16), xmm6); // H * 2
+  __ movdqu(xmm0, xmm6);
+  for (int i = 2; i < 9; i++) {
+      gfmul_avx2(xmm6, xmm0);
+      __ movdqu(Address(htbl, i * 16), xmm6);
+  }
+  __ ret(0);
+}
+
+void StubGenerator::ghash8_encrypt8_parallel(Register key, Register subkeyHtbl, XMMRegister ctr_blockx, XMMRegister aad_hashx,
+                                             Register in, Register out, Register ct, Register pos, bool in_order, Register rounds) {
+
+  const XMMRegister t1 = xmm0;
+  const XMMRegister t2 = xmm10;
+  const XMMRegister t3 = xmm11;
+  const XMMRegister t4 = xmm12;
+  const XMMRegister t5 = xmm13;
+  const XMMRegister t6 = xmm14;
+  const XMMRegister t7 = xmm15;
+  Label skip_reload, last_aes_rnd, aes_192, aes_256;
+
+  __ movdqu(t2, xmm1);
+  __ movdqu(Address(rsp, 16 * 0), xmm2);
+  __ movdqu(Address(rsp, 16 * 1), xmm3);
+  __ movdqu(Address(rsp, 16 * 2), xmm4);
+  __ movdqu(Address(rsp, 16 * 3), xmm5);
+  __ movdqu(Address(rsp, 16 * 4), xmm6);
+  __ movdqu(Address(rsp, 16 * 5), xmm7);
+  __ movdqu(Address(rsp, 16 * 6), xmm8);
+
+  if (in_order) {
+    __ vpaddd(xmm1, ctr_blockx, ExternalAddress(counter_mask_linc1_addr()), Assembler::AVX_128bit, rbx /*rscratch*/); //Increment counter by 1
+    __ movdqu(t5, ExternalAddress(counter_mask_linc2_addr()), rbx /*rscratch*/);
+    __ vpaddd(xmm2, ctr_blockx, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm3, xmm1, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm4, xmm2, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm5, xmm3, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm6, xmm4, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm7, xmm5, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm8, xmm6, t5, Assembler::AVX_128bit);
+    __ movdqu(ctr_blockx, xmm8);
+
+    __ movdqu(t5, ExternalAddress(counter_shuffle_mask_addr()), rbx /*rscratch*/);
+    __ vpshufb(xmm1, xmm1, t5, Assembler::AVX_128bit); //perform a 16Byte swap
+    __ vpshufb(xmm2, xmm2, t5, Assembler::AVX_128bit); //perform a 16Byte swap
+    __ vpshufb(xmm3, xmm3, t5, Assembler::AVX_128bit); //perform a 16Byte swap
+    __ vpshufb(xmm4, xmm4, t5, Assembler::AVX_128bit); //perform a 16Byte swap
+    __ vpshufb(xmm5, xmm5, t5, Assembler::AVX_128bit); //perform a 16Byte swap
+    __ vpshufb(xmm6, xmm6, t5, Assembler::AVX_128bit); //perform a 16Byte swap
+    __ vpshufb(xmm7, xmm7, t5, Assembler::AVX_128bit); //perform a 16Byte swap
+    __ vpshufb(xmm8, xmm8, t5, Assembler::AVX_128bit); //perform a 16Byte swap
+  } else {
+    __ vpaddd(xmm1, ctr_blockx, ExternalAddress(counter_mask_linc1f_addr()), Assembler::AVX_128bit, rbx /*rscratch*/); //Increment counter by 1
+    __ vmovdqu(t5, ExternalAddress(counter_mask_linc2f_addr()), Assembler::AVX_128bit, rbx /*rscratch*/);
+    __ vpaddd(xmm2, ctr_blockx, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm3, xmm1, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm4, xmm2, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm5, xmm3, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm6, xmm4, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm7, xmm5, t5, Assembler::AVX_128bit);
+    __ vpaddd(xmm8, xmm6, t5, Assembler::AVX_128bit);
+    __ movdqu(ctr_blockx, xmm8);
+  }
+
+  load_key(t1, key, 16 * 0, rbx /*rscratch*/);
+  __ vpxor(xmm1, xmm1, t1, Assembler::AVX_128bit);
+  __ vpxor(xmm2, xmm2, t1, Assembler::AVX_128bit);
+  __ vpxor(xmm3, xmm3, t1, Assembler::AVX_128bit);
+  __ vpxor(xmm4, xmm4, t1, Assembler::AVX_128bit);
+  __ vpxor(xmm5, xmm5, t1, Assembler::AVX_128bit);
+  __ vpxor(xmm6, xmm6, t1, Assembler::AVX_128bit);
+  __ vpxor(xmm7, xmm7, t1, Assembler::AVX_128bit);
+  __ vpxor(xmm8, xmm8, t1, Assembler::AVX_128bit);
+
+  load_key(t1, key, 16 * 1, rbx /*rscratch*/);
+  __ aesenc(xmm1, t1);
+  __ aesenc(xmm2, t1);
+  __ aesenc(xmm3, t1);
+  __ aesenc(xmm4, t1);
+  __ aesenc(xmm5, t1);
+  __ aesenc(xmm6, t1);
+  __ aesenc(xmm7, t1);
+  __ aesenc(xmm8, t1);
+
+  load_key(t1, key, 16 * 2, rbx /*rscratch*/);
+  __ aesenc(xmm1, t1);
+  __ aesenc(xmm2, t1);
+  __ aesenc(xmm3, t1);
+  __ aesenc(xmm4, t1);
+  __ aesenc(xmm5, t1);
+  __ aesenc(xmm6, t1);
+  __ aesenc(xmm7, t1);
+  __ aesenc(xmm8, t1);
+
+  __ movdqu(t5, (Address(subkeyHtbl, 8 * 16)));
+  __ vpclmulqdq(t4, t2, t5, 0x11); //t4 = a1*b1
+  __ vpclmulqdq(t7, t2, t5, 0x00); //t7 = a0*b0
+  __ vpclmulqdq(t6, t2, t5, 0x01); //t6 = a1*b0
+  __ vpclmulqdq(t5, t2, t5, 0x10); //t5 = a0*b1
+  __ vpxor(t6, t6, t5, Assembler::AVX_128bit);
+
+  load_key(t1, key, 16 * 3, rbx /*rscratch*/);
+  __ aesenc(xmm1, t1);
+  __ aesenc(xmm2, t1);
+  __ aesenc(xmm3, t1);
+  __ aesenc(xmm4, t1);
+  __ aesenc(xmm5, t1);
+  __ aesenc(xmm6, t1);
+  __ aesenc(xmm7, t1);
+  __ aesenc(xmm8, t1);
+
+  __ movdqu(t1, Address(rsp, 16 * 0));
+  __ movdqu(t5, (Address(subkeyHtbl, 7 * 16)));
+  __ vpclmulqdq(t3, t1, t5, 0x11);
+  __ vpxor(t4, t4, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x00);
+  __ vpxor(t7, t7, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x01);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x10);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  load_key(t1, key, 16 * 4, rbx /*rscratch*/);
+  __ aesenc(xmm1, t1);
+  __ aesenc(xmm2, t1);
+  __ aesenc(xmm3, t1);
+  __ aesenc(xmm4, t1);
+  __ aesenc(xmm5, t1);
+  __ aesenc(xmm6, t1);
+  __ aesenc(xmm7, t1);
+  __ aesenc(xmm8, t1);
+
+  __ movdqu(t1, Address(rsp, 16 * 1));
+  __ movdqu(t5, (Address(subkeyHtbl, 6 * 16)));
+  __ vpclmulqdq(t3, t1, t5, 0x11);
+  __ vpxor(t4, t4, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x00);
+  __ vpxor(t7, t7, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x01);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x10);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  load_key(t1, key, 16 * 5, rbx /*rscratch*/);
+  __ aesenc(xmm1, t1);
+  __ aesenc(xmm2, t1);
+  __ aesenc(xmm3, t1);
+  __ aesenc(xmm4, t1);
+  __ aesenc(xmm5, t1);
+  __ aesenc(xmm6, t1);
+  __ aesenc(xmm7, t1);
+  __ aesenc(xmm8, t1);
+
+  __ movdqu(t1, Address(rsp, 16 * 2));
+  __ movdqu(t5, (Address(subkeyHtbl, 5 * 16)));
+  __ vpclmulqdq(t3, t1, t5, 0x11);
+  __ vpxor(t4, t4, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x00);
+  __ vpxor(t7, t7, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x01);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x10);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  load_key(t1, key, 16 * 6, rbx /*rscratch*/);
+  __ aesenc(xmm1, t1);
+  __ aesenc(xmm2, t1);
+  __ aesenc(xmm3, t1);
+  __ aesenc(xmm4, t1);
+  __ aesenc(xmm5, t1);
+  __ aesenc(xmm6, t1);
+  __ aesenc(xmm7, t1);
+  __ aesenc(xmm8, t1);
+
+  __ movdqu(t1, Address(rsp, 16 * 3));
+  __ movdqu(t5, (Address(subkeyHtbl, 4 * 16)));
+  __ vpclmulqdq(t3, t1, t5, 0x11);
+  __ vpxor(t4, t4, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x00);
+  __ vpxor(t7, t7, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x01);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x10);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  load_key(t1, key, 16 * 7, rbx /*rscratch*/);
+  __ aesenc(xmm1, t1);
+  __ aesenc(xmm2, t1);
+  __ aesenc(xmm3, t1);
+  __ aesenc(xmm4, t1);
+  __ aesenc(xmm5, t1);
+  __ aesenc(xmm6, t1);
+  __ aesenc(xmm7, t1);
+  __ aesenc(xmm8, t1);
+
+  __ movdqu(t1, Address(rsp, 16 * 4));
+  __ movdqu(t5, (Address(subkeyHtbl, 3 * 16)));
+  __ vpclmulqdq(t3, t1, t5, 0x11);
+  __ vpxor(t4, t4, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x00);
+  __ vpxor(t7, t7, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x01);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x10);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  load_key(t1, key, 16 * 8, rbx /*rscratch*/);
+  __ aesenc(xmm1, t1);
+  __ aesenc(xmm2, t1);
+  __ aesenc(xmm3, t1);
+  __ aesenc(xmm4, t1);
+  __ aesenc(xmm5, t1);
+  __ aesenc(xmm6, t1);
+  __ aesenc(xmm7, t1);
+  __ aesenc(xmm8, t1);
+
+  __ movdqu(t1, Address(rsp, 16 * 5));
+  __ movdqu(t5, (Address(subkeyHtbl, 2 * 16)));
+  __ vpclmulqdq(t3, t1, t5, 0x11);
+  __ vpxor(t4, t4, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x00);
+  __ vpxor(t7, t7, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x01);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x10);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  load_key(t1, key, 16 * 9, rbx /*rscratch*/);
+  __ aesenc(xmm1, t1);
+  __ aesenc(xmm2, t1);
+  __ aesenc(xmm3, t1);
+  __ aesenc(xmm4, t1);
+  __ aesenc(xmm5, t1);
+  __ aesenc(xmm6, t1);
+  __ aesenc(xmm7, t1);
+  __ aesenc(xmm8, t1);
+
+  __ movdqu(t1, Address(rsp, 16 * 6));
+  __ movdqu(t5, (Address(subkeyHtbl, 1 * 16)));
+
+  __ vpclmulqdq(t3, t1, t5, 0x00);
+  __ vpxor(t7, t7, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x01);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x10);
+  __ vpxor(t6, t6, t3, Assembler::AVX_128bit);
+
+  __ vpclmulqdq(t3, t1, t5, 0x11);
+  __ vpxor(t1, t4, t3, Assembler::AVX_128bit);
+
+  __ vpslldq(t3, t6, 8, Assembler::AVX_128bit); //shift-L t3 2 DWs
+  __ vpsrldq(t6, t6, 8, Assembler::AVX_128bit); //shift-R t2 2 DWs
+  __ vpxor(t7, t7, t3, Assembler::AVX_128bit);
+  __ vpxor(t1, t1, t6, Assembler::AVX_128bit); // accumulate the results in t1:t7
+
+  load_key(t5, key, 16 * 10, rbx /*rscratch*/);
+  __ cmpl(rounds, 52);
+  __ jcc(Assembler::greaterEqual, aes_192);
+  __ jmp(last_aes_rnd);
+  __ bind(aes_192);
+  __ aesenc(xmm1, t5);
+  __ aesenc(xmm2, t5);
+  __ aesenc(xmm3, t5);
+  __ aesenc(xmm4, t5);
+  __ aesenc(xmm5, t5);
+  __ aesenc(xmm6, t5);
+  __ aesenc(xmm7, t5);
+  __ aesenc(xmm8, t5);
+  load_key(t5, key, 16 * 11, rbx /*rscratch*/);
+  __ aesenc(xmm1, t5);
+  __ aesenc(xmm2, t5);
+  __ aesenc(xmm3, t5);
+  __ aesenc(xmm4, t5);
+  __ aesenc(xmm5, t5);
+  __ aesenc(xmm6, t5);
+  __ aesenc(xmm7, t5);
+  __ aesenc(xmm8, t5);
+  load_key(t5, key, 16 * 12, rbx /*rscratch*/);
+  __ cmpl(rounds, 60);
+  __ jcc(Assembler::aboveEqual, aes_256);
+  __ jmp(last_aes_rnd);
+
+  __ bind(aes_256);
+  __ aesenc(xmm1, t5);
+  __ aesenc(xmm2, t5);
+  __ aesenc(xmm3, t5);
+  __ aesenc(xmm4, t5);
+  __ aesenc(xmm5, t5);
+  __ aesenc(xmm6, t5);
+  __ aesenc(xmm7, t5);
+  __ aesenc(xmm8, t5);
+  load_key(t5, key, 16 * 13, rbx /*rscratch*/);
+  __ aesenc(xmm1, t5);
+  __ aesenc(xmm2, t5);
+  __ aesenc(xmm3, t5);
+  __ aesenc(xmm4, t5);
+  __ aesenc(xmm5, t5);
+  __ aesenc(xmm6, t5);
+  __ aesenc(xmm7, t5);
+  __ aesenc(xmm8, t5);
+  load_key(t5, key, 16 * 14, rbx /*rscratch*/);
+  __ bind(last_aes_rnd);
+  __ aesenclast(xmm1, t5);
+  __ aesenclast(xmm2, t5);
+  __ aesenclast(xmm3, t5);
+  __ aesenclast(xmm4, t5);
+  __ aesenclast(xmm5, t5);
+  __ aesenclast(xmm6, t5);
+  __ aesenclast(xmm7, t5);
+  __ aesenclast(xmm8, t5);
+
+  __ movdqu(t2, Address(in, pos, Address::times_1, 16 * 0));
+  __ vpxor(xmm1, xmm1, t2, Assembler::AVX_128bit);
+  __ movdqu(t2, Address(in, pos, Address::times_1, 16 * 1));
+  __ vpxor(xmm2, xmm2, t2, Assembler::AVX_128bit);
+  __ movdqu(t2, Address(in, pos, Address::times_1, 16 * 2));
+  __ vpxor(xmm3, xmm3, t2, Assembler::AVX_128bit);
+  __ movdqu(t2, Address(in, pos, Address::times_1, 16 * 3));
+  __ vpxor(xmm4, xmm4, t2, Assembler::AVX_128bit);
+  __ movdqu(t2, Address(in, pos, Address::times_1, 16 * 4));
+  __ vpxor(xmm5, xmm5, t2, Assembler::AVX_128bit);
+  __ movdqu(t2, Address(in, pos, Address::times_1, 16 * 5));
+  __ vpxor(xmm6, xmm6, t2, Assembler::AVX_128bit);
+  __ movdqu(t2, Address(in, pos, Address::times_1, 16 * 6));
+  __ vpxor(xmm7, xmm7, t2, Assembler::AVX_128bit);
+  __ movdqu(t2, Address(in, pos, Address::times_1, 16 * 7));
+  __ vpxor(xmm8, xmm8, t2, Assembler::AVX_128bit);
+
+  //first phase of the reduction
+  __ vmovdqu(t3, ExternalAddress(ghash_polynomial_reduction_addr()), Assembler::AVX_128bit, rbx /*rscratch*/);
+
+  __ vpclmulqdq(t2, t3, t7, 0x01);
+  __ vpslldq(t2, t2, 8, Assembler::AVX_128bit); //shift-L xmm2 2 DWs
+
+  __ vpxor(t7, t7, t2, Assembler::AVX_128bit); //first phase of the reduction complete
+
+  //Write to the Ciphertext buffer
+  __ movdqu(Address(out, pos, Address::times_1, 16 * 0), xmm1);
+  __ movdqu(Address(out, pos, Address::times_1, 16 * 1), xmm2);
+  __ movdqu(Address(out, pos, Address::times_1, 16 * 2), xmm3);
+  __ movdqu(Address(out, pos, Address::times_1, 16 * 3), xmm4);
+  __ movdqu(Address(out, pos, Address::times_1, 16 * 4), xmm5);
+  __ movdqu(Address(out, pos, Address::times_1, 16 * 5), xmm6);
+  __ movdqu(Address(out, pos, Address::times_1, 16 * 6), xmm7);
+  __ movdqu(Address(out, pos, Address::times_1, 16 * 7), xmm8);
+
+  __ cmpptr(ct, out);
+  __ jcc(Assembler::equal, skip_reload);
+  __ movdqu(xmm1, Address(in, pos, Address::times_1, 16 * 0));
+  __ movdqu(xmm2, Address(in, pos, Address::times_1, 16 * 1));
+  __ movdqu(xmm3, Address(in, pos, Address::times_1, 16 * 2));
+  __ movdqu(xmm4, Address(in, pos, Address::times_1, 16 * 3));
+  __ movdqu(xmm5, Address(in, pos, Address::times_1, 16 * 4));
+  __ movdqu(xmm6, Address(in, pos, Address::times_1, 16 * 5));
+  __ movdqu(xmm7, Address(in, pos, Address::times_1, 16 * 6));
+  __ movdqu(xmm8, Address(in, pos, Address::times_1, 16 * 7));
+
+  __ bind(skip_reload);
+  //second phase of the reduction
+  __ vpclmulqdq(t2, t3, t7, 0x00);
+  __ vpsrldq(t2, t2, 4, Assembler::AVX_128bit); //shift-R t2 1 DW (Shift-R only 1-DW to obtain 2-DWs shift-R)
+
+  __ vpclmulqdq(t4, t3, t7, 0x10);
+  __ vpslldq(t4, t4, 4, Assembler::AVX_128bit); //shift-L t4 1 DW (Shift-L 1-DW to obtain result with no shifts)
+  __ vpxor(t4, t4, t2, Assembler::AVX_128bit); //second phase of the reduction complete
+  __ vpxor(t1, t1, t4, Assembler::AVX_128bit); //the result is in t1
+
+  //perform a 16Byte swap
+  __ vpshufb(xmm1, xmm1, ExternalAddress(counter_shuffle_mask_addr()), Assembler::AVX_128bit, rbx /*rscratch*/);
+  __ vpshufb(xmm2, xmm2, ExternalAddress(counter_shuffle_mask_addr()), Assembler::AVX_128bit, rbx /*rscratch*/);
+  __ vpshufb(xmm3, xmm3, ExternalAddress(counter_shuffle_mask_addr()), Assembler::AVX_128bit, rbx /*rscratch*/);
+  __ vpshufb(xmm4, xmm4, ExternalAddress(counter_shuffle_mask_addr()), Assembler::AVX_128bit, rbx /*rscratch*/);
+  __ vpshufb(xmm5, xmm5, ExternalAddress(counter_shuffle_mask_addr()), Assembler::AVX_128bit, rbx /*rscratch*/);
+  __ vpshufb(xmm6, xmm6, ExternalAddress(counter_shuffle_mask_addr()), Assembler::AVX_128bit, rbx /*rscratch*/);
+  __ vpshufb(xmm7, xmm7, ExternalAddress(counter_shuffle_mask_addr()), Assembler::AVX_128bit, rbx /*rscratch*/);
+  __ vpshufb(xmm8, xmm8, ExternalAddress(counter_shuffle_mask_addr()), Assembler::AVX_128bit, rbx /*rscratch*/);
+  __ vpxor(xmm1, xmm1, t1, Assembler::AVX_128bit);
+}
