+    // Opens named log file in ${test.classes}
+    static void initLogFile() {
+        if (initialized)
+            return;
+
+        try {
+            Path path = Path.of(logDir, "statetest.txt");
+            FileOutputStream f = new FileOutputStream(path.toFile(), true);
+            p = new PrintStream(f);
+        } catch (Exception e) {}
+        initialized = true;
+    }
+
+    static void println(String msg) {
+        initLogFile();
+        p.println(msg);
+    }
