@@ -404,0 +408,10 @@
+bool SubTasksDone::try_claim_task(uint t) {
+  assert(t < _n_tasks, "bad task id.");
+  uint old = _tasks[t];
+  if (old == 0) {
+    old = Atomic::cmpxchg(&_tasks[t], 0u, 1u);
+  }
+  bool res = old == 0;
+  return res;
+}
