-                                        } \
-                                    } while(0)
+//
+// Checks for an exception and if one occurred,
+// pops 'pops' local frames and frees 'x' before
+// returning NULL
+//
+#define POP_EXCEPTION_CHECK_AND_FREE(pops, x) do { \
+                                                  if ((*env)->ExceptionCheck(env)) { \
+                                                      int i; \
+                                                      for (i = 0; i < pops; i++) { \
+                                                          (*env)->PopLocalFrame(env, NULL); \
+                                                      } \
+                                                      free(x); \
+                                                      return NULL; \
+                                                  } \
+                                              } while(0)
