+    private static URLConnection createAndConnectURLConnection() throws IOException {
+        final URL url = Path.of(System.getProperty("java.io.tmpdir")).toUri().toURL();
+        final URLConnection conn = url.openConnection();
+        conn.connect();
+        return conn;
+    }
