@@ -357,0 +357,5 @@
+static void reportFcloseExit(FILE *fptr, const char* fmt, const char* arg) {
+    if (fmt != NULL) JLI_ReportMessage(fmt, arg);
+    if (fptr != NULL) fclose(fptr);
+    exit(1);
+}
