@@ -253,0 +253,10 @@
+    #ifdef BLKGETSIZE64
+    if (S_ISBLK(sb.st_mode)) {
+        uint64_t size;
+        if(ioctl(fd, BLKGETSIZE64, &size) < 0) {
+            return -1;
+        }
+        return (jlong)size;
+    }
+    #endif
+    return sb.st_size;
