+    public static class Int extends ArraysSort {
+
+        @Param
+        private Type type;
+
+        int[] gold;
+
+        public enum Type {
+
+            RANDOM {
+                @Override
+                void build(int[] a, Random random) {
+                    for (int i = 0; i < a.length; ++i) {
+                        a[i] = random.nextInt();
+                    }
+                }
+            },
+
+            REPEATED {
+                @Override
+                void build(int[] a, Random random) {
+                    for (int i = 0; i < a.length; ++i) {
+                        a[i] = i % 7;
+                    }
+                }
+            },
+
+            STAGGER {
+                @Override
+                void build(int[] a, Random random) {
+                    for (int i = 0; i < a.length; ++i) {
+                        a[i] = (i * 5) % a.length;
+                    }
+                }
+            },
+
+            SHUFFLE {
+                @Override
+                void build(int[] a, Random random) {
+                    for (int i = 0, j = 0, k = 1; i < a.length; ++i) {
+                        a[i] = random.nextInt(6) > 0 ? (j += 2) : (k += 2);
+                    }
+                }
+            };
+
+            abstract void build(int[] a, Random random);
+        }
+
+        @Setup
+        public void setup() {
+            gold = new int[size];
+        }
+
+        @Setup(Level.Invocation)
+        public void init() {
+            type.build(gold, random);
+        }
+
+        @Benchmark
+        public void testSort() {
+            Arrays.sort(gold);
+        }
+
+        @Benchmark
+        public void testParallelSort() {
+            Arrays.parallelSort(gold);
+        }
+    }
