+        ByteArrayOutputStream ba = new ByteArrayOutputStream();
+        for (dtype v : ops) {
+            if (++index < ops.size()) {
+                int olen = cipher.getOutputSize(plen) + offset; // output length
+                if (plen > offset) {
+                    inofs = offset;
