+import org.testng.Assert;
+import org.testng.annotations.AfterMethod;
+import org.testng.annotations.BeforeMethod;
+import org.testng.annotations.DataProvider;
+import org.testng.annotations.Test;
+
+import java.io.IOException;
+import java.io.InputStream;
+import java.io.OutputStream;
+import java.nio.charset.StandardCharsets;
+import java.nio.file.FileSystem;
+import java.nio.file.FileSystems;
+import java.nio.file.Files;
+import java.nio.file.Path;
+import java.util.Map;
+import java.util.Random;
+
+
+/**
+ * @test
+ * @summary Verify that the outputstream created for zip file entries, through the ZipFileSystem
+ * works fine for varying sizes of the zip file entries and various different values of tempFileThreshold
+ * @bug 8190753
+ * @run testng ZipFSOutputStreamTest
+ */
+public class ZipFSOutputStreamTest {
+    // List of files to be added to the ZIP file along with their sizes in bytes
+    private static final Map<String, Long> ZIP_ENTRIES = Map.of(
+            "LargeSize", 25L * 1024L * 1024L, // 25 MB
+            "d1/SmallSize", 1234L,
+            "d1/d2/ZeroSize", 0L);
+
+    private static final Path ZIP_FILE = Path.of("zipfs-outputstream-test.zip");
+
+    @BeforeMethod
+    public void setUp() throws IOException {
+        deleteFiles();
+    }
+
+    @AfterMethod
+    public void tearDown() throws IOException {
+        deleteFiles();
+    }
+
+    private static void deleteFiles() throws IOException {
+        Files.deleteIfExists(ZIP_FILE);
+    }
+
+    @DataProvider(name = "zipfsThresholdValueParsing")
+    private Object[][] zipfsThresholdValueParsing() {
+        // various different values passed to the "tempFileThreshold", some valid some invalid
+        return new Object[][]{
+                {Map.of("create", "true", "tempFileThreshold", "1024")},
+                {Map.of("create", "true", "tempFileThreshold", "1")},
+                {Map.of("create", "true", "tempFileThreshold", "-1")},
+                {Map.of("create", "true", "tempFileThreshold", "-2")},
+                {Map.of("create", "true", "tempFileThreshold", "0")},
+                {Map.of("create", "true", "tempFileThreshold", Integer.MAX_VALUE)},
+                {Map.of("create", "true", "tempFileThreshold", Long.MAX_VALUE)},
+                {Map.of("create", "true", "tempFileThreshold", "helloworld")},
+                {Map.of("create", "true", "tempFileThreshold", 124232.232d)},
+                {Map.of("create", "true", "tempFileThreshold", 122.232f)}
+        };
+    }
+
+    /**
+     * Create a zip filesystem with various different values of tempFileThreshold, some valid and
+     * some invalid. Make sure that in all these cases, the zip filesystem creation works fine
+     * and entries can be added to the zip file and read from and the content is as expected.
+     */
+    @Test(dataProvider = "zipfsThresholdValueParsing")
+    public void testThresholdValueParsing(final Map<String, ?> env) throws Exception {
+        try (final FileSystem zipfs = FileSystems.newFileSystem(ZIP_FILE, env)) {
+            // create the zip file with just a simple entry
+            try (final OutputStream os = Files.newOutputStream(zipfs.getPath("helloworld.txt"))) {
+                os.write("hi".getBytes(StandardCharsets.UTF_8));
+            }
+            // now verify the written content
+            try (final InputStream is = Files.newInputStream(zipfs.getPath("helloworld.txt"))) {
+                Assert.assertEquals(is.readAllBytes(), "hi".getBytes(StandardCharsets.UTF_8),
+                        "Unexpected content in zip entry");
+            }
+        }
+    }
+
+    @DataProvider(name = "zipfsVaryingThreshold")
+    private Object[][] zipfsVaryingThreshold() {
+        return new Object[][]{
+                // default tempfile threshold, for both DEFLATED and STORED compression modes
+                {Map.of("create", "true", "noCompression", "true")},
+                {Map.of("create", "true", "noCompression", "false")},
+                // specific threshold for both DEFLATED and STORED compression modes
+                {Map.of("create", "true", "noCompression", "true", "tempFileThreshold", "1024")},
+                {Map.of("create", "true", "noCompression", "false", "tempFileThreshold", "1024")},
+                // threshold disabled
+                {Map.of("create", "true", "noCompression", "true", "tempFileThreshold", "-1")},
+                {Map.of("create", "true", "noCompression", "false", "tempFileThreshold", "-1")},
+                {Map.of("create", "true", "noCompression", "true", "tempFileThreshold", "0")},
+                {Map.of("create", "true", "noCompression", "false", "tempFileThreshold", "0")},
+
+        };
+    }
+
+    /**
+     * Create a zip filesystem using different outputstream temp file creation threshold and write
+     * out entries of varying sizes. Then verify that the generated zip file entries are as expected,
+     * both in size and content
+     */
+    @Test(dataProvider = "zipfsVaryingThreshold")
+    public void testOutputStreamWithDifferentThreshold(final Map<String, ?> env) throws Exception {
+        final byte[] chunk = new byte[1024];
+        new Random().nextBytes(chunk);
+        try (final FileSystem zipfs = FileSystems.newFileSystem(ZIP_FILE, env)) {
+            // create the zip with varying sized entries
+            for (final Map.Entry<String, Long> entry : ZIP_ENTRIES.entrySet()) {
+                final Path entryPath = zipfs.getPath(entry.getKey());
+                if (entryPath.getParent() != null) {
+                    Files.createDirectories(entryPath.getParent());
+                }
+                try (final OutputStream os = Files.newOutputStream(entryPath)) {
+                    writeAsChunks(os, chunk, entry.getValue());
+                }
+            }
+        }
+        // now verify the written content
+        try (final FileSystem zipfs = FileSystems.newFileSystem(ZIP_FILE)) {
+            for (final Map.Entry<String, Long> entry : ZIP_ENTRIES.entrySet()) {
+                final Path entryPath = zipfs.getPath(entry.getKey());
+                try (final InputStream is = Files.newInputStream(entryPath)) {
+                    final byte[] buf = new byte[chunk.length];
+                    int numRead;
+                    long totalRead = 0;
+                    while ((numRead = is.read(buf)) != -1) {
+                        totalRead += numRead;
+                        // verify the content
+                        for (int i = 0, chunkoffset = (int) ((totalRead - numRead) % chunk.length);
+                             i < numRead; i++, chunkoffset++) {
+                            Assert.assertEquals(buf[i], chunk[chunkoffset % chunk.length],
+                                    "Unexpected content in " + entryPath);
+                        }
+                    }
+                    Assert.assertEquals(totalRead, (long) entry.getValue(),
+                            "Unexpected number of bytes read from zip entry " + entryPath);
+                }
+            }
+        }
+    }
+
+    /**
+     * Repeatedly writes out to the outputstream, the chunk of data, till the number of bytes
+     * written to the stream equals the totalSize
+     */
+    private static void writeAsChunks(final OutputStream os, final byte[] chunk,
+                                      final long totalSize) throws IOException {
+        long remaining = totalSize;
+        for (long l = 0; l < totalSize; l += chunk.length) {
+            final int numToWrite = (int) Math.min(remaining, chunk.length);
+            os.write(chunk, 0, numToWrite);
+            remaining -= numToWrite;
+        }
+    }
+}
