@@ -191,0 +191,2 @@
+     * Subclassers must ensure this is set to a non-null value
+     * during construction and not set this to null.
@@ -1315,3 +1317,5 @@
-        JDesktopIcon oldValue = getDesktopIcon();
-        desktopIcon = d;
-        firePropertyChange("desktopIcon", oldValue, d);
+        if (d != null) {
+            JDesktopIcon oldValue = getDesktopIcon();
+            desktopIcon = d;
+            firePropertyChange("desktopIcon", oldValue, d);
+        }
