+  // Code for java.lang.Thread::onSpinWait() intrinsic.
+  void spin_wait() {
+#define EMIT_N_INST(n, inst) for (int i = 0; i < (n); ++i) inst()
+
+    int inst_count = VM_Version::pause_impl_desc().inst_count();
+    switch (VM_Version::pause_impl_desc().inst()) {
+      case NOP:
+        EMIT_N_INST(inst_count, nop);
+        break;
+      case ISB:
+        EMIT_N_INST(inst_count, isb);
+        break;
+      case YIELD:
+        EMIT_N_INST(inst_count, yield);
+        break;
+      default:
+        ShouldNotReachHere();
+    }
+#undef EMIT_N_INST
+  }
+

@@ -49,0 +49,25 @@
+PauseImplDesc VM_Version::_pause_impl_desc;
+
+static PauseImplDesc get_pause_impl_desc() {
+  const char *s = OnSpinWaitImpl;
+  unsigned int count = 1;
+  if (isdigit(*s)) {
+    count = *s - '0';
+    if (count == 0) {
+      vm_exit_during_initialization("Invalid value for OnSpinWaitImpl: zero instruction count", OnSpinWaitImpl);
+    }
+    s += 1;
+  }
+  if (strcmp(s, "nop") == 0) {
+    return PauseImplDesc(NOP, count);
+  } else if (strcmp(s, "isb") == 0) {
+    return PauseImplDesc(ISB, count);
+  } else if (strcmp(s, "yield") == 0) {
+    return PauseImplDesc(YIELD, count);
+  } else if (strcmp(s, "none") != 0) {
+    vm_exit_during_initialization("Invalid value for OnSpinWaitImpl", OnSpinWaitImpl);
+  }
+
+  return PauseImplDesc{};
+}
